<style scoped>


.speed-dial-item {
  display: grid;
  color: var(--umatt-c-white); /* Use custom color */
  grid-template-columns: 1fr 3fr;
  justify-content: space-between;
  padding: 1rem;
  align-items: center;
  gap: 1rem;
  background-color: rgba(0, 0, 0, 0.7); /* Add background color for better readability */
  border-radius: 8px;
  cursor: pointer;
}

.speed-dial-item span {
  font-size: 1.5rem;
}

.speed-dial-item span:nth-child(2) {
  font-size: 1rem;
}

.speed-dial-item:hover {
  background-color: rgba(255, 255, 255, 0.2); /* Lighten on hover */
}

.speed-dial-item:active {
  background-color: rgba(255, 255, 255, 0.3); /* Lighten more on active */
}

.hero-container {
  position: relative;
  overflow: hidden;
  height: 100vh;
  width: 100%;
}

.hero {
  display: flex;
  position: relative;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
 

}
.hero::before{
  content: '';
  position: absolute;
  inset:0;
  width: 100%;
  height: 100%;
  background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('@/assets/images/competition-group-photo-2024.jpg') no-repeat  center center;
  animation: zoom-in 7s ease-in-out forwards;
  background-size: cover; /* Ensure the background image covers the entire container */
  z-index: 0;
}

.hero__content {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap:1rem;
  margin-top: 2rem;
  padding: 2rem;
  z-index: 2;
}

.hero svg {
  height: clamp(4.25rem, 12vw, 16.5rem);
  margin-top: 10px;
  overflow: hidden;
}

.hero .name {
  font-size: clamp(2.25rem, 4.5vw, 5rem);
  color: var(--umatt-c-red); /* Use custom color */
  width:0;
  overflow: hidden;
}

.hero .description {
  margin-top: 1rem;
  font-size: clamp(1rem, 3.5vw, 2.5rem);
  color: var(--umatt-c-white); /* Use custom color */
  margin-top: 10px;
  animation: fade-in 2s ease-in-out 6s forwards;
  opacity: 0;
}

svg path {
  fill: transparent;
  stroke: white; /* Use custom color */
  stroke-width: 0.5;
  stroke-dasharray: 251;
  stroke-dashoffset: 251;
  animation: draw 4.5s ease-in-out forwards, fill 2s ease-in-out 3.5s forwards;
}

.logo {
  width: 120px;
  height: auto;
  opacity: 0;
  animation: fade-in 3s ease-in-out 6s forwards, pulse 2s infinite;
}



@keyframes zoom-in {
  to {
    transform: scale(1.12);
  }
}

@keyframes draw {
  to {
    stroke-dashoffset: 0;
  }
}

